import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EditBlog = () => {
  const { id } = useParams();
  const [blogTitle, setBlogTitle] = useState('');
  const [blogText, setBlogText] = useState('');
  const [blogPost, setBlogPost] = useState(null);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    fetch(`http://localhost:5000/blog/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setBlogPost(data);
        setBlogTitle(data.title);
        setBlogText(data.text);
      })
      .catch((error) => console.error('Error fetching blog post:', error));
  }, [id]);

  const handleBlogTitleChange = (e) => {
    setBlogTitle(e.target.value);
  };

  const handleBlogTextChange = (e) => {
    setBlogText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/blog/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title: blogTitle, text: blogText }),
    })
      .then((response) => response.json())
      .then((data) => {
        window.location.href = '/admin/blog';
      })
      .catch((error) => console.error('Error updating blog post:', error));
  };

  return (
    <div className="container mt-5">
      {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
          <h2>Изменить пост в блоге</h2>
          {blogPost ? (
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="blogTitle" className="form-label">
                  Заголовок
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="blogTitle"
                  value={blogTitle}
                  onChange={handleBlogTitleChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="blogText" className="form-label">
                  Текст
                </label>
                <textarea
                  className="form-control"
                  id="blogText"
                  rows="5"
                  value={blogText}
                  onChange={handleBlogTextChange}
                  required
                ></textarea>
              </div>
              <button type="submit" className="btn btn-primary">
                Сохранить изменения
              </button>
            </form>
          ) : (
            <p>Loading...</p>
          )}
        </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default EditBlog;
