// Файл OffcanvasCart.jsx
import React from 'react';
import { useCart } from './CartContext';

const OffcanvasCart = () => {
  const { cartItems, removeFromCart } = useCart();

  return (
    <div>
      <h2>Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <li key={item.id}>
            {item.name}{' '}
            <button onClick={() => removeFromCart(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OffcanvasCart;
