import React, { useState, useEffect } from 'react';
import { useParams, Redirect } from 'react-router-dom';

const EditBrand = () => {
  const { id } = useParams();
  const [brand, setBrand] = useState({
    id: null,
    name: '',
  });
  const [isBrandUpdated, setIsBrandUpdated] = useState(false);

  useEffect(() => {
    // Получаем данные бренда для редактирования
    fetch(`http://localhost:5000/brands/${id}`)
      .then((response) => response.json())
      .then((data) => setBrand(data))
      .catch((error) => console.error('Error fetching brand:', error));
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBrand({
      ...brand,
      [name]: value,
    });
  };

  const handleUpdateBrand = () => {
    // Отправляем запрос на обновление бренда
    fetch(`http://localhost:5000/brands/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(brand),
    })
      .then(() => setIsBrandUpdated(true))
      .catch((error) => console.error('Error updating brand:', error));
  };

  if (isBrandUpdated) {
    // После успешного обновления перенаправляем на страницу списка брендов
    return <Redirect to="/admin/brands" />;
  }

  return (
    <div className="container mt-5">
      <h2>Редактировать бренд</h2>
      <form>
        <div className="mb-3">
          <label htmlFor="brandName" className="form-label">
            Название бренда
          </label>
          <input
            type="text"
            className="form-control"
            id="brandName"
            name="name"
            value={brand.name}
            onChange={handleInputChange}
          />
        </div>
        <button type="button" className="btn btn-primary" onClick={handleUpdateBrand}>
          Сохранить изменения
        </button>
      </form>
    </div>
  );
};

export default EditBrand;
