                        </ul>
                     </div>
                  </div>
               </div>
            </div>
            {/* header navigation area end */}
         </header>
         <Content />
         <OffcanvasCart isOpen={isCartOpen} closeOffcanvas={closeCart} />
      <OffcanvasWishlist isOpen={isWishlistOpen} closeOffcanvas={closeWishlist} />
      </>
   );
};

