import React, { useState, useEffect } from 'react';

const AddBlog = () => {
  const [blogTitle, setBlogTitle] = useState('');
  const [blogText, setBlogText] = useState('');
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleBlogTitleChange = (e) => {
    setBlogTitle(e.target.value);
  };

  const handleBlogTextChange = (e) => {
    setBlogText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/blog', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title: blogTitle, text: blogText }),
    })
      .then((response) => response.json())
      .then((data) => {
        window.location.href = '/admin/blog';
      })
      .catch((error) => console.error('Error adding blog post:', error));
  };

  return (
    <div className="container mt-5">
      {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
          <h2>Добавить новый пост в блог</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="blogTitle" className="form-label">
                Заголовок
              </label>
              <input
                type="text"
                className="form-control"
                id="blogTitle"
                value={blogTitle}
                onChange={handleBlogTitleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="blogText" className="form-label">
                Текст
              </label>
              <textarea
                className="form-control"
                id="blogText"
                rows="5"
                value={blogText}
                onChange={handleBlogTextChange}
                required
              ></textarea>
            </div>
            <button type="submit" className="btn btn-primary">
              Добавить пост
            </button>
          </form>
        </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default AddBlog;
