import React, { useState, useEffect } from 'react';

class EditBrand extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      brandName: '',
      brandId: props.match.params.id,
    };
  }

  componentDidMount() {
    // Загрузим данные о бренде для редактирования
    fetch(`http://localhost:5000/brands/${this.state.brandId}`)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          brandName: data.name,
        });
      })
      .catch((error) => console.error('Error fetching brand:', error));
  }

  handleBrandNameChange = (e) => {
    this.setState({ brandName: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    // Отправим данные для обновления бренда
    fetch(`http://localhost:5000/brands/${this.state.brandId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: this.state.brandName }),
    })
      .then((response) => response.json())
      .then(() => {
        // После успешного обновления бренда перенаправляем на страницу списка брендов
        window.location.href = '/admin/brands';
      })
      .catch((error) => console.error('Error updating brand:', error));
  };

  render() {
    return (
      <div className="container mt-5">
        <h2>Изменить бренд</h2>
        <form onSubmit={this.handleSubmit}>
          <div className="mb-3">
            <label htmlFor="brandName" className="form-label">
              Название бренда
            </label>
            <input
              type="text"
              className="form-control"
              id="brandName"
              value={this.state.brandName}
              onChange={this.handleBrandNameChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Изменить бренд
          </button>
        </form>
      </div>
    );
  }
}

export default EditBrand;
