// Profile.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Здесь вы можете использовать свой URL для получения данных о пользователе
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Предполагается, что у вас есть токен после входа в систему
          },
        });
        
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <>
    {/* Breadcrumb Area */}
    <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
    <div className="container">
      <div className="row align-items-center justify-content-center">
        <div className="col-12 text-center">
          <h2 className="breadcrumb-title">Profile</h2>
          <ul className="breadcrumb-list">
            <li className="breadcrumb-item"><a href="/">Home</a></li>
            <li className="breadcrumb-item active">Profile</li>
          </ul>
        </div>
      </div>
    </div>
  </div>
    <div className="container mt-5">
      {/* Информация о пользователе */}
      {userData && (
        <div>
          <h2>{userData.username}'s Profile</h2>
          <p>Email: {userData.email}</p>
          <p>Registration Date: {new Date(userData.date_reg).toLocaleDateString()}</p>
          {/* Дополнительные данные о пользователе, которые у вас есть в базе данных */}
        </div>
      )}

      {/* Отображение заказов пользователя (пример) */}
      {userData && userData.orders && userData.orders.length > 0 && (
        <div>
          <h3 className="mt-4">User Orders</h3>
          <ul className="list-group">
            {userData.orders.map((order) => (
              <li key={order.orderNum} className="list-group-item">
                <strong>Order {order.orderNum}</strong> - {order.status.name} - Total: ${order.total}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
    </>
  );
};

export default Profile;
