import React, { useState, useEffect } from 'react';

const AddProduct = () => {
  const [name, setName] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [brandId, setBrandId] = useState('');
  const [description, setDescription] = useState('');
  const [cost, setCost] = useState('');
  const [mainImage, setMainImage] = useState('');
  const [additionalImages, setAdditionalImages] = useState([]);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setCategoryId(e.target.value);
  };

  const handleBrandChange = (e) => {
    setBrandId(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleCostChange = (e) => {
    setCost(e.target.value);
  };

  const handleMainImageChange = (e) => {
    setMainImage(e.target.value);
  };

  const handleAdditionalImagesChange = (e) => {
    const files = Array.from(e.target.files);
    const images = files.map((file) => URL.createObjectURL(file));
    setAdditionalImages(images);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('category_id', categoryId);
    formData.append('brand_id', brandId);
    formData.append('description', description);
    formData.append('cost', cost);
    formData.append('mainImage', mainImage);
    additionalImages.forEach((image) => formData.append('additionalImages', image));

    try {
      const response = await fetch('http://localhost:5000/products', {
        method: 'POST',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.ok) {
        window.location.href = '/admin/products';
      } else {
        console.error('Failed to add product');
      }
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div className="container mt-5">
      {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
          <h2>Добавить новый товар</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Название товара
              </label>
              <input type="text" className="form-control" id="name" value={name} onChange={handleNameChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="category" className="form-label">
                Категория
              </label>
              <select className="form-select" id="category" value={categoryId} onChange={handleCategoryChange} required>
                <option value="">Выберите категорию</option>
                {/* Здесь нужно добавить код для загрузки и отображения списка категорий */}
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="brand" className="form-label">
                Бренд
              </label>
              <select className="form-select" id="brand" value={brandId} onChange={handleBrandChange} required>
                <option value="">Выберите бренд</option>
                {/* Здесь нужно добавить код для загрузки и отображения списка брендов */}
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Описание
              </label>
              <textarea className="form-control" id="description" rows="5" value={description} onChange={handleDescriptionChange} required></textarea>
            </div>
            <div className="mb-3">
              <label htmlFor="cost" className="form-label">
                Стоимость
              </label>
              <input type="number" className="form-control" id="cost" value={cost} onChange={handleCostChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="mainImage" className="form-label">
                Основное изображение
              </label>
              <input type="text" className="form-control" id="mainImage" value={mainImage} onChange={handleMainImageChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="additionalImages" className="form-label">
                Дополнительные изображения (до 5)
              </label>
              <input type="file" className="form-control" id="additionalImages" onChange={handleAdditionalImagesChange} accept="image/*" multiple />
            </div>
            <button type="submit" className="btn btn-primary">
              Добавить товар
            </button>
          </form>
        </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default AddProduct;
