import React, { useState, useEffect } from 'react';
import { useCart } from '../components/CartContext';

// Создаем и экспортируем компонент Checkout
const Checkout = () => {
  // Получаем данные о корзине из контекста
  const { cartItems } = useCart();

  // Состояния для данных формы
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [city, setCity] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');

  // Генерация случайного числа в пределах 100000-999999
  const generateRandomNum = () => {
    return Math.floor(100000 + Math.random() * 900000);
  };

  // Генерация случайного orderNum
  const generateOrderNum = () => {
    return `ORDER${generateRandomNum()}`;
  };

  // Генерация случайного checkNum
  const generateCheckNum = () => {
    return `CHECK${generateRandomNum()}`;
  };

  // Обработчик кнопки "Place Order"
  const handlePlaceOrder = async () => {
    // Создаем нового пользователя (вам может понадобиться своя логика создания пользователя)
    const customerResponse = await fetch('http://localhost:5000/customers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: `${firstName} ${lastName}`,
        phone,
        email,
        address: `${streetAddress}, ${city}`, // Используем city в составе адреса
      }),
    });

    const customerData = await customerResponse.json();
    const customerId = customerData.id;

    // Создаем заказ
    const orderResponse = await fetch('http://localhost:5000/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        customer_id: customerId,
        date: new Date(),
        status_id: 1, // Устанавливаем начальный статус заказа (может потребоваться настройка)
      }),
    });

    const orderData = await orderResponse.json();
    const orderNum = orderData.id; // Используем id заказа как уникальный номер заказа

    // Создаем детали заказа
    for (const item of cartItems) {
      await fetch('http://localhost:5000/orderdetails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderNum,
          prod_code: item.id,
          productQuantity: item.quantity,
        }),
      });
    }

    // Создаем платеж с сгенерированным checkNum
    await fetch('http://localhost:5000/payments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        customer_id: customerId,
        checkNum: generateCheckNum(),
        payment: new Date(),
        amount: cartItems.reduce((total, item) => total + item.cost * item.quantity, 0),
      }),
    });

    // Очищаем корзину после успешного оформления заказа
    // (здесь должна быть функция из вашего контекста для очистки корзины)
    // clearCart();

    // Перенаправляем пользователя на страницу подтверждения заказа или другую нужную страницу
    // (здесь должна быть функция из вашего роутера для перенаправления)
    // redirectToConfirmationPage();
  };

  return (
    <>
          {/* Breadcrumb Area */}
          <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h2 className="breadcrumb-title">Checkout</h2>
              <ul className="breadcrumb-list">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">Checkout</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    <div className="checkout-area pt-100px pb-100px">
      <div className="container">
        {/* Ваш остальной код */}
        <div className="row">
        <div class="col-lg-7">
                        <div class="billing-info-wrap">
                            <h3>Billing Details</h3>
                            <div class="row">
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>First Name</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Last Name</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-12">
                  <div className="billing-select mb-4">
                    <label>City</label>
                    <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
                      <option>Select a city</option>
                      {cities.map((city) => (
                        <option key={city.id} value={city.name}>
                          {city.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                                <div class="col-lg-12">
                                    <div class="billing-info mb-4">
                                        <label>Street Address</label>
                                        <input class="billing-address" placeholder="House number and street name" type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Phone</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Email Address</label>
                                        <input type="text" />
                                    </div>
                                </div>
                            </div>
                        </div>
          </div>
          <div className="col-lg-5 mt-md-30px mt-lm-30px">
            <div className="your-order-area">
              <h3>Your order</h3>
              <div className="your-order-wrap gray-bg-4">
                <div className="your-order-product-info">
                  <div className="your-order-top">
                    <ul>
                      <li>Product</li>
                      <li>Total</li>
                    </ul>
                  </div>
                  <div className="your-order-middle">
                    <ul>
                      {cartItems.map((item) => (
                        <li key={item.id}>
                          <span className="order-middle-left">{item.name} X {item.quantity}</span>{' '}
                          <span className="order-price">${item.cost * item.quantity} </span>
                        </li>
                      ))}
                    </ul>
                  </div>
                  <div className="your-order-bottom">
                    <ul>
                      <li className="your-order-shipping">Shipping</li>
                      <li>Free shipping</li>
                    </ul>
                  </div>
                  <div className="your-order-total">
                    <ul>
                      <li className="order-total">Total</li>
                      <li>${cartItems.reduce((total, item) => total + item.cost * item.quantity, 0)}</li>
                    </ul>
                  </div>
                </div>
                <div className="payment-method">
                  {/* ... (остальной код) */}
                </div>
              </div>
              <div className="Place-order mt-25">
        <button className="btn-hover" onClick={handlePlaceOrder}>
          Place Order
        </button>
      </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};

export default Checkout;
