// Profile.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Fetch user details and orders from the server
    const fetchProfileData = async () => {
      try {
        // Replace 'userId' with the actual user ID or retrieve it from your authentication system
        const userId = 1; // Example user ID
        const userResponse = await axios.get(`http://localhost:5000/users/${userId}`);
        setUserDetails(userResponse.data);

        const ordersResponse = await axios.get(`http://localhost:5000/orders/user/${userId}`);
        setOrders(ordersResponse.data);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    };

    fetchProfileData();
  }, []);

  return (
    <div>
      {/* Breadcrumb area */}
      <div className="breadcrumb-area">
        {/* ... (breadcrumb code) */}
      </div>

      {/* Account area */}
      <div className="account-dashboard pt-100px pb-100px">
        <div className="container">
          <div className="row">
            <div className="col-sm-12 col-md-3 col-lg-3">
              {/* Dashboard tabs */}
              {/* ... (dashboard tabs code) */}
            </div>
            <div className="col-sm-12 col-md-9 col-lg-9">
              {/* Tab content */}
              <div className="tab-content dashboard_content">
                {/* Orders tab */}
                <div className="tab-pane fade" id="orders">
                  <h4>Orders</h4>
                  <div className="table_page table-responsive">
                    <table>
                      {/* Orders table */}
                      {/* ... (orders table code) */}
                    </table>
                  </div>
                </div>

                {/* Address tab */}
                <div className="tab-pane" id="address">
                  {/* Address information */}
                  {/* ... (address information code) */}
                </div>

                {/* Account details tab */}
                <div className="tab-pane fade" id="account-details">
                  <h3>Account details</h3>
                  <div className="login">
                    <div className="login_form_container">
                      <div className="account_login_form">
                        <form action="#">
                          {/* User details form */}
                          {/* ... (user details form code) */}
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
