import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const SingleProduct = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [images, setImages] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    const fetchImages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products/${id}/images`);
        setImages(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/comments/${id}`);
        setComments(response.data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchProduct();
    fetchImages();
    fetchComments();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {/* ... (existing code) ... */}

      {/* Product Reviews */}
      <div className="row">
        <div className="col-lg-12">
          <div className="review-wrapper">
            {/* Map through comments */}
            {comments.map((comment) => (
              <div key={comment.id} className="single-review">
                <div className="review-img">
                  {/* You can display user avatar or image here */}
                  <img src="assets/images/review-image/1.png" alt={`Reviewer ${comment.user_id}`} />
                </div>
                <div className="review-content">
                  <div className="review-top-wrap">
                    <div className="review-left">
                      <div className="review-name">
                        <h4>{comment.users.name}</h4>
                      </div>
                      <div className="rating-product">
                        {/* You can display user rating here */}
                        {/* For example, if comment.rating is available */}
                        {/* Display appropriate number of stars */}
                      </div>
                    </div>
                    <div className="review-left">
                      <span>{comment.date}</span>
                    </div>
                  </div>
                  <div className="review-bottom">
                    <p>{comment.com_text}</p>
                  </div>
                </div>
              </div>
            ))}
            {/* End of comment mapping */}
          </div>
        </div>
        {/* Add a Review Form */}
        <div className="col-lg-12">
          <div className="ratting-form-wrapper pl-50">
            <h3>Add a Review</h3>
            <div className="ratting-form">
              <form action="#">
                {/* Rating Stars Input */}
                {/* Add your logic to handle user's rating input */}
                {/* You can use a library like react-star-ratings */}
                <div className="star-box">
                  <span>Your rating:</span>
                  {/* Display user's selected rating here */}
                </div>
                {/* Other Form Fields */}
                <div className="row">
                  <div className="col-md-6">
                    <div className="rating-form-style">
                      <input placeholder="Name" type="text" />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="rating-form-style">
                      <input placeholder="Email" type="email" />
                    </div>
                  </div>
                  <div className="col-md-12">
                    <div className="rating-form-style form-submit">
                      <textarea name="Your Review" placeholder="Message"></textarea>
                      <button className="btn btn-primary btn-hover-color-primary " type="submit" value="Submit">
                        Submit
                      </button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
