// CartContext.jsx
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState(getCartFromLocalStorage());

  const addToCart = async (itemId) => {
    try {
      const response = await axios.get(`http://localhost:5000/products/${itemId}`);
      const newItem = response.data;
      setCartItems((prevItems) => {
        const updatedCart = [...prevItems, newItem];
        saveCartToLocalStorage(updatedCart);
        return updatedCart;
      });
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  const removeFromCart = (itemId) => {
    setCartItems((prevItems) => {
      const updatedCart = prevItems.filter((item) => item.id !== itemId);
      saveCartToLocalStorage(updatedCart);
      return updatedCart;
    });
  };

  const getCartFromLocalStorage = () => {
    const storedCart = localStorage.getItem('cart');
    return storedCart ? JSON.parse(storedCart) : [];
  };

  const saveCartToLocalStorage = (cart) => {
    localStorage.setItem('cart', JSON.stringify(cart));
  };

  useEffect(() => {
    const prevItems = getCartFromLocalStorage();
    setCartItems(prevItems);
  }, []); // Добавляем зависимость, чтобы useEffect выполнялся только при монтировании компонента

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
