import React, { useState, useEffect, useRef } from 'react';
import Content from './Content';
import OffcanvasCart from './OffcanvasCart';
import OffcanvasWishlist from './OffcanvasWishlist';
import { useCart } from './CartContext';
import { useWishlist } from './WishlistContext';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';

const Header = () => {
   const { cartItems } = useCart();
   const { wishlistItems } = useWishlist();
   const isAuthenticated = !!window.localStorage.getItem('token');

   const [isCartOpen, setIsCartOpen] = useState(false);
   const [isWishlistOpen, setIsWishlistOpen] = useState(false);

   const [searchTerm, setSearchTerm] = useState('');
   const [products, setProducts] = useState([]);
   const [userRole, setUserRole] = useState(null);
   const [showDropdown, setShowDropdown] = useState(false);
   const dropdownRef = useRef(null);

   useEffect(() => {
      const handleClickOutside = (event) => {
         if (
            dropdownRef.current &&
            !dropdownRef.current.contains(event.target)
         ) {
            setShowDropdown(false);
         }
      };

      document.addEventListener('mousedown', handleClickOutside);

      return () => {
         document.removeEventListener('mousedown', handleClickOutside);
      };
   }, []);

   useEffect(() => {
      const fetchUserData = async () => {
         try {
            const response = await fetch('http://localhost:5000/users/me', {
               headers: {
                  Authorization: `Bearer ${localStorage.getItem('token')}`,
               },
            });
            const userData = await response.json();

            setUserRole(userData.role);
         } catch (error) {
            console.error('Error fetching user data:', error);
         }
      };

      fetchUserData();
   }, []);

   useEffect(() => {
      const fetchProducts = async () => {
         try {
            const response = await fetch('http://localhost:5000/products/');
            const data = await response.json();
            setProducts(data);
         } catch (error) {
            console.error('Error fetching products:', error);
         }
      };

      fetchProducts();
   }, []);

   const filteredProducts = products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
   );

   const openCart = () => {
      setIsCartOpen(true);
      setIsWishlistOpen(false);
   };

   const closeCart = () => {
      setIsCartOpen(false);
   };

   const openWishlist = () => {
      setIsWishlistOpen(true);
      setIsCartOpen(false);
   };

   const closeWishlist = () => {
      setIsWishlistOpen(false);
   };

   const handleSearchSubmit = (e) => {
      e.preventDefault();
      if (filteredProducts.length > 0) {
         const firstProduct = filteredProducts[0];
         window.location.href = `/single-product/${firstProduct.id}`;
      }
   };

   return (
      <>
         <header>
            <Navbar bg="light" expand="lg">
               <Navbar.Brand href="/">Site Logo</Navbar.Brand>
               <Navbar.Toggle aria-controls="basic-navbar-nav" />
               <Navbar.Collapse id="basic-navbar-nav">
                  <Nav className="mr-auto">
                     <Nav.Link href="/">Home</Nav.Link>
                     <Nav.Link href="/about">About</Nav.Link>
                     <Nav.Link href="/shop-left-sidebar">Shop</Nav.Link>
                     <Nav.Link href="/blog-list">Blog</Nav.Link>
                     <Nav.Link href="/contact">Contact</Nav.Link>
                     {(userRole === 'admin' || userRole === 'client_manager') && (
                        <Nav.Link href="/admin">Admin</Nav.Link>
                     )}
                  </Nav>
                  <Nav>
                     <Nav.Link href="tel:0123456789">
                        <i className="fa fa-phone"></i> +372 5555 5556
                     </Nav.Link>
                     <Nav.Link href="mailto:demo@example.com">
                        <i className="fa fa-envelope"></i> elshop@store.com
                     </Nav.Link>
                     {isAuthenticated ? (
                        <Nav.Link href="/profile">
                           <i className="fa fa-user"></i> Account
                        </Nav.Link>
                     ) : (
                        <Nav.Link href="/login">
                           <i className="fa fa-user"></i> Account
                        </Nav.Link>
                     )}
                  </Nav>
               </Navbar.Collapse>
            </Navbar>
         </header>
         <Content />
         <OffcanvasCart isOpen={isCartOpen} closeOffcanvas={closeCart} />
         <OffcanvasWishlist isOpen={isWishlistOpen} closeOffcanvas={closeWishlist} />
      </>
   );
};

export default Header;
