import React, { useState, useEffect } from 'react';

const EditBrand = ({ match }) => {
  const [brandName, setBrandName] = useState('');
  const [brand, setBrand] = useState(null);

  useEffect(() => {
    // Получаем данные о бренде по ID при загрузке компонента
    fetch(`http://localhost:5000/brands/${match.params.id}`)
      .then((response) => response.json())
      .then((data) => {
        setBrand(data);
        setBrandName(data.name);
      })
      .catch((error) => console.error('Error fetching brand:', error));
  }, [match.params.id]);

  const handleBrandNameChange = (e) => {
    setBrandName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Отправляем запрос на обновление данных бренда
    fetch(`http://localhost:5000/brands/${match.params.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: brandName }),
    })
      .then((response) => response.json())
      .then((data) => {
        // После успешного обновления перенаправляем на страницу списка брендов
        window.location.href = '/admin/brands';
      })
      .catch((error) => console.error('Error updating brand:', error));
  };

  return (
    <div className="container mt-5">
      <h2>Изменить бренд</h2>
      {brand ? (
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="brandName" className="form-label">
              Название бренда
            </label>
            <input
              type="text"
              className="form-control"
              id="brandName"
              value={brandName}
              onChange={handleBrandNameChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Сохранить изменения
          </button>
        </form>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default EditBrand;
