import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const BrandList = () => {
  const [brands, setBrands] = useState([]);

  useEffect(() => {
    // Здесь вы можете использовать свой API endpoint для получения списка брендов
    const fetchBrands = async () => {
      try {
        const response = await fetch('http://localhost:5000/brand');
        const data = await response.json();
        setBrands(data);
      } catch (error) {
        console.error('Error fetching brands:', error);
      }
    };

    fetchBrands();
  }, []);

  const handleDelete = async (id) => {
    try {
      // Здесь вы можете использовать свой API endpoint для удаления бренда
      await fetch(`http://localhost:5000/brand/${id}`, {
        method: 'DELETE',
      });
      // Обновляем список брендов после удаления
      setBrands((prevBrands) => prevBrands.filter((brand) => brand.id !== id));
    } catch (error) {
      console.error('Error deleting brand:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Список брендов</h2>
      <Link to="/admin/brands/add" className="btn btn-primary mb-3">
        Добавить новый бренд
      </Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Название бренда</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {brands.map((brand) => (
            <tr key={brand.id}>
              <td>{brand.id}</td>
              <td>{brand.name}</td>
              <td>
                <Link to={`/admin/brands/edit/${brand.id}`} className="btn btn-warning me-2">
                  Изменить
                </Link>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(brand.id)}
                >
                  Удалить
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandList;
