import React, { useState, useEffect } from 'react';
import Content from './Content';
import OffcanvasCart from './OffcanvasCart';
import OffcanvasWishlist from './OffcanvasWishlist';
import { useCart } from './CartContext';
import { useWishlist } from './WishlistContext';

const Header = () => {
  const { cartItems } = useCart();
  const { wishlistItems } = useWishlist();
  const isAuthenticated = !!window.localStorage.getItem('token');

  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isWishlistOpen, setIsWishlistOpen] = useState(false);

  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const userData = await response.json();

        // Сохраняем роль пользователя в состоянии
        setUserRole(userData.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/products/');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
    setSelectedProductId(null); // Сбрасываем выбранный продукт при изменении поискового запроса
  };

  const handleProductClick = (productId) => {
   console.log({productId});
    setSelectedProductId(productId);
  };

  const handleButtonClick = () => {
    if (selectedProductId) {
      // Программное перенаправление на страницу продукта
      window.location.href = `./single-product/${selectedProductId}`;
    }
  };

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );
 
  const openCart = () => {
    setIsCartOpen(true);
    setIsWishlistOpen(false);
  };

  const closeCart = () => {
    setIsCartOpen(false);
  };

  const openWishlist = () => {
    setIsWishlistOpen(true);
    setIsCartOpen(false);
  };

  const closeWishlist = () => {
    setIsWishlistOpen(false);
  };

   return (
      <>
         <header>
            {/* Header top area start */}
            <div className="header-top">
               <div className="container">
                  <div className="row justify-content-between align-items-center">
                     <div className="col">
                        <div className="welcome-text">
                           <p>Best shop in the world</p>
                        </div>
                     </div>
                     <div className="col d-none d-lg-block">
                        <div className="top-nav">
                           <ul>
                              <li>
                                 <a href="tel:0123456789">
                                    <i className="fa fa-phone"></i> +372 5555
                                    5556
                                 </a>
                              </li>
                              <li>
                                 <a href="mailto:demo@example.com">
                                    <i className="fa fa-envelope"></i>{' '}
                                    elshop@store.com
                                 </a>
                              </li>
                              {isAuthenticated ? (
          // Если пользователь аутентифицирован, перенаправляем на /profile
          <li>
            <a href="/profile">
              <i className="fa fa-user"></i> Account
            </a>
          </li>
        ) : (
          // Если пользователь не аутентифицирован, перенаправляем на /login
          <li>
            <a href="/login">
              <i className="fa fa-user"></i> Account
            </a>
          </li>
        )}
                           </ul>
                        </div>
                     </div>
                  </div>
               </div>
            </div>
            {/* Header top area end */}
            <div className="header-bottom d-none d-lg-block">
              <div className="container">
                <div className="row justify-content-between align-items-center">
                  <div className="col-lg-3 col">
                    <div className="header-logo">
                      <a href="/"><img src="/images/logo/logo.png" alt="Site Logo" /></a>
                    </div>
                  </div>
                  <div className="col-lg-6 d-none d-lg-block">
                    <div className="search-element">
                    <form>
      <input
        type="text"
        placeholder="Search"
        value={searchTerm}
        onChange={handleInputChange}
        list="productsList"
      />
      <datalist id="productsList">
        {filteredProducts.map((product) => (
          <option
            key={product.id}
            value={product.name}
            onClick={() => handleProductClick(product.id)}
          />
        ))}
      </datalist>
      {selectedProductId ? (
        // Если выбран продукт, кнопка становится ссылкой
        <a href={`./single-product/${selectedProductId}`}>
          <i className="pe-7s-search"></i>
        </a>
      ) : (
        // Иначе кнопка остается
        <button type="button" onClick={handleButtonClick}>
          <i className="pe-7s-search"></i>
        </button>
      )}
    </form>
                    </div>
                  </div>
                  <div className="col-lg-3 col">
                  <div className="header-actions">
          <a
            href="#offcanvas-wishlist"
            className="header-action-btn offcanvas-toggle"
            onClick={openWishlist}
          >
            <i className="pe-7s-like"></i>
            <span className="header-action-num">{wishlistItems.length}</span>
          </a>
          <a
            href="#offcanvas-cart"
            className="header-action-btn header-action-btn-cart offcanvas-toggle pr-0"
            onClick={openCart}
          >
            <i className="pe-7s-shopbag"></i>
            <span className="header-action-num">{cartItems.length}</span>
          </a>
          <a
            href="#offcanvas-mobile-menu"
            className="header-action-btn header-action-btn-menu offcanvas-toggle d-lg-none"
          >
            <i className="pe-7s-menu"></i>
          </a>
        </div>
                  </div>
                </div>
              </div>
            </div>
            {/* Header action area end */}
            {/* Header action area start */}
            <div className="header-bottom d-lg-none sticky-nav style-1">
               <div className="container">
                  <div className="row justify-content-between align-items-center">
                     <div className="col-lg-3 col">
                        <div className="header-logo">
                           <a href="index.html">
                              <img
                                 src="/images/logo/logo.png"
                                 alt="Site Logo"
                              />
                           </a>
                        </div>
                     </div>
                     <div className="col-lg-3 col">
                        <div className="header-actions">
                           <a
                              href="#offcanvas-wishlist"
                              className="header-action-btn offcanvas-toggle"
                           >
                              <i className="pe-7s-like"></i>
                           </a>
                           <a
                              href="#offcanvas-cart"
                              className="header-action-btn header-action-btn-cart offcanvas-toggle pr-0"
                           >
                              <i className="pe-7s-shopbag"></i>
                              <span className="header-action-num">01</span>
                           </a>
                           <a
                              href="#offcanvas-mobile-menu"
                              className="header-action-btn header-action-btn-menu offcanvas-toggle d-lg-none"
                           >
                              <i className="pe-7s-menu"></i>
                           </a>
                        </div>
                     </div>
                  </div>
               </div>
            </div>
            {/* Header action area end */}
            {/* header navigation area start */}
            <div className="header-nav-area d-none d-lg-block sticky-nav">
               <div className="container">
                  <div className="header-nav">
                     <div className="main-menu position-relative">
                        <ul>
                           <li className="dropdown">
                              <a href="/">Home</a>
                           </li>
                           <li>
                              <a href="/about">About</a>
                           </li>
                           <li>
                              <a href="/shop-left-sidebar">Shop</a>
                           </li>
                           <li>
                              <a href="/blog-list">Blog</a>
                           </li>
                           <li>
                              <a href="/contact">Contact</a>
                           </li>
                           <li>
                              <a href="/admin">Admin</a>
                           </li>
                        </ul>
                     </div>
                  </div>
               </div>
            </div>
            {/* header navigation area end */}
         </header>
         <Content />
         <OffcanvasCart isOpen={isCartOpen} closeOffcanvas={closeCart} />
      <OffcanvasWishlist isOpen={isWishlistOpen} closeOffcanvas={closeWishlist} />
      </>
   );
};

export default Header;
