// Файл OffcanvasWishlist.jsx
import React from 'react';
import { useWishlist } from './WishlistContext';

const OffcanvasWishlist = () => {
  const { wishlistItems, removeFromWishlist } = useWishlist();

  return (
    <div id="offcanvas-wishlist" className="offcanvas offcanvas-wishlist">
      <div className="inner">
        <div className="head">
          <span className="title">Wishlist</span>
          <button className="offcanvas-close">×</button>
        </div>
        <div className="body customScroll">
          <ul className="minicart-product-list">
            {wishlistItems.map((item) => (
              <li key={item.id}>
                <a href={`single-product/${item.id}`} className="image">
                  <img src={item.image} alt={item.name} />
                </a>
                <div className="content">
                  <a href={`single-product/${item.id}`} className="title">
                    {item.name}
                  </a>
                  <span className="quantity-price">
                    1 x <span className="amount">${Number(item.price).toFixed(2)}</span>
                  </span>
                  <button className="remove" onClick={() => removeFromWishlist(item.id)}>
                    ×
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <div className="foot">
          <div className="buttons">
            <a href="wishlist.html" className="btn btn-dark btn-hover-primary mt-30px">
              view wishlist
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OffcanvasWishlist;
