import React, { useState, useEffect } from 'react';

const EditBrand = ({ match }) => {
  const brandId = match.params.id;
  const [brandName, setBrandName] = useState('');

  useEffect(() => {
    // Здесь вы можете использовать ваш API для получения данных о выбранном бренде
    fetch(`http://localhost:5000/brands/${brandId}`)
      .then((response) => response.json())
      .then((data) => setBrandName(data.name))
      .catch((error) => console.error('Error fetching brand:', error));
  }, [brandId]);

  const handleBrandNameChange = (e) => {
    setBrandName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь вы можете использовать ваш API для отправки обновленных данных на сервер
    fetch(`http://localhost:5000/brand/${brandId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: brandName }),
    })
      .then((response) => response.json())
      .then((data) => {
        // После успешного обновления бренда перенаправляем на страницу списка брендов
        window.location.href = '/admin/brands';
      })
      .catch((error) => console.error('Error updating brand:', error));
  };

  return (
    <div className="container mt-5">
      <h2>Редактировать бренд</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="brandName" className="form-label">
            Название бренда
          </label>
          <input
            type="text"
            className="form-control"
            id="brandName"
            value={brandName}
            onChange={handleBrandNameChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Обновить бренд
        </button>
      </form>
    </div>
  );
};

export default EditBrand;
