import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState(getCartFromLocalStorage());

  const addToCart = async (itemId) => {
    try {
      const response = await axios.get(`http://localhost:5000/products/${itemId}`);
      const newItem = response.data;
      setCartItems((prevItems) => [...prevItems, newItem]);
      saveCartToLocalStorage([...prevItems, newItem]);
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  const removeFromCart = (itemId) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
    saveCartToLocalStorage(prevItems.filter((item) => item.id !== itemId));
  };

  // ...

  // Функция для получения корзины из локального хранилища
  const getCartFromLocalStorage = () => {
    const storedCart = localStorage.getItem('cart');
    return storedCart ? JSON.parse(storedCart) : [];
  };

  // Функция для сохранения корзины в локальное хранилище
  const saveCartToLocalStorage = (cart) => {
    localStorage.setItem('cart', JSON.stringify(cart));
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};
