import React, { useState } from 'react';

const AddBrand = () => {
  const [brandName, setBrandName] = useState('');

  const handleBrandNameChange = (e) => {
    setBrandName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь вы можете использовать ваш API для отправки данных на сервер
    fetch('http://localhost:5000/brands', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: brandName }),
    })
      .then((response) => response.json())
      .then((data) => {
        // После успешного добавления бренда перенаправляем на страницу списка брендов
        window.location.href = '/admin/brands';
      })
      .catch((error) => console.error('Error adding brand:', error));
  };

  return (
    <div className="container mt-5">
      <h2>Добавить новый бренд</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="brandName" className="form-label">
            Название бренда
          </label>
          <input
            type="text"
            className="form-control"
            id="brandName"
            value={brandName}
            onChange={handleBrandNameChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Добавить бренд
        </button>
      </form>
    </div>
  );
};

export default AddBrand;
