import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Swiper from 'swiper';
import 'swiper/swiper-bundle.css';

const SingleProduct = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [images, setImages] = useState([]);
  const [comments, setComments] = useState([]);
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    // Инициализация Swiper
    new Swiper('.swiper-container', {
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
    });
  }, [images]);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    const fetchImages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/products/${id}/images`);
        setImages(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/comments/${id}`);
        setComments(response.data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchProduct();
    fetchImages();
    fetchComments();
  }, [id]);

  useEffect(() => {
    const fetchRelatedProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');
        // Get four random products excluding the current one
        const filteredProducts = response.data.filter((p) => p.id !== parseInt(id, 10));
        const randomProducts = [];

        while (randomProducts.length < 4 && filteredProducts.length > 0) {
          const randomIndex = Math.floor(Math.random() * filteredProducts.length);
          randomProducts.push(filteredProducts.splice(randomIndex, 1)[0]);
        }

        setRelatedProducts(randomProducts);
      } catch (error) {
        console.error('Error fetching related products:', error);
      }
    };
    fetchRelatedProducts();
  }, [])

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {/* Breadcrumb Area */}
      {/* ... (unchanged) ... */}
      
      {/* Product Details Area */}
      <div className="product-details-area pt-100px pb-100px">
        <div className="container">
          <div className="row">
          <div className="col-lg-6 col-sm-12 col-xs-12 mb-lm-30px mb-md-30px mb-sm-30px">
              {/* Swiper */}
              <div className="swiper-container">
  <div className="swiper-wrapper">
    {/* Main Images */}
    {images.map((image, index) => (
      <div key={index} className="swiper-slide">
        <img className="img-responsive m-auto" src={image.link} alt={`Product ${product.id}`} />
        <a className="venobox full-preview" data-gall="myGallery" href={image.link}>
          <i className="fa fa-arrows-alt" aria-hidden="true"></i>
        </a>
      </div>
    ))}
    {/* Additional Images */}
    {images.map((image, index) => (
      <div key={index} className="swiper-slide">
        <img className="img-responsive m-auto" src={image.link} alt={`Product ${product.id}`} />
      </div>
    ))}
  </div>
  {/* Add Navigation */}
  <div className="swiper-button-next"></div>
  <div className="swiper-button-prev"></div>
</div>
              </div>
            </div>

            <div className="col-lg-6 col-sm-12 col-xs-12" data-aos="fade-up" data-aos-delay="200">
              {/* Product Details Content */}
              <div className="product-details-content quickview-content ml-25px">
                <h2>{product.name}</h2>
                {/* Pricing Meta */}
                <div className="pricing-meta">
                  <ul className="d-flex">
                    <li className="new-price">${product.cost}</li>
                  </ul>
                </div>

                {/* Product Rating */}
                <div className="pro-details-rating-wrap">
                  <div className="rating-product">
                    <i className="fa fa-star"></i>
                    <i className="fa fa-star"></i>
                    <i className="fa fa-star"></i>
                    <i className="fa fa-star"></i>
                    <i className="fa fa-star"></i>
                  </div>
                </div>

                {/* Product Description */}
                <p className="mt-30px">Description:<br></br>{product.description}</p>


                {/* Categories */}
                <div className="pro-details-categories-info pro-details-same-style d-flex m-0">
                  <span>Categories: </span>
                  <ul className="d-flex">
                    <li><a href="#">{product.category.name} </a></li>
                  </ul>
                </div>

                {/* Tags */}
                <div className="pro-details-categories-info pro-details-same-style d-flex m-0">
                  <span>Brand: </span>
                  <ul className="d-flex">
                    <li><a href="#">{product.brand.name}</a></li>
                  </ul>
                </div>

                {/* Product Quantity */}
                <div className="pro-details-quality">
                  <div className="cart-plus-minus">
                    <input className="cart-plus-minus-box" type="text" name="qtybutton" placeholder="1" />
                  </div>

                  {/* Add to Cart Button */}
                  <div className="pro-details-cart">
                    <button className="add-cart"> Add To Cart</button>
                  </div>

                  {/* Wishlist and Compare Buttons */}
                  <div className="pro-details-compare-wishlist pro-details-wishlist ">
                    <a href="wishlist.html"><i className="pe-7s-like"></i></a>
                  </div>
                </div>
              </div>
              
              {/* Product Details Description Area */}
              <div className="description-review-wrapper">
                <div className="description-review-topbar nav">
                  <button className="active" data-bs-toggle="tab" data-bs-target="#des-details1">Description</button>
                  <button data-bs-toggle="tab" data-bs-target="#des-details3">Comments</button>
                </div>
                <div className="tab-content description-review-bottom">
                  <div id="des-details1" className="tab-pane active">
                    {/* Product Description */}
                    <div className="product-description-wrapper">
                      <p>{product.description}</p>
                    </div>
                  </div>
                  <div id="des-details3" className="tab-pane">
                    {/* Product Comments */}
                    <div className="row">
                      <div className="col-lg-12">
                        <div className="comment-wrapper">
                          {/* Single Comment */}
                          {comments.map((comment) => (
                            <div key={comment.id} className="single-comment">
                              <div className="comment-img">
                                <img src="https://template.hasthemes.com/hmart/hmart/assets/images/review-image/1.png" alt={`User ${comment.user_id}`} />
                              </div>
                              <div className="comment-content">
                                <div className="comment-top-wrap">
                                  <div className="comment-left" style={{marginBottom:'50px'}}>
                                    <div className="comment-name">
                                      <h4>{comment.users.username}</h4>
                                    </div>
                                    <div className="comment-right">
                                  <p>{comment.com_text}</p>
                                </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                      <div className="col-lg-12">
                        {/* Add a Comment Form */}
                        <div className="comment-form-wrapper pl-50">
                          <h3>Add a Comment</h3>
                          <div className="comment-form">
                            <form action="#">
                              <div className="row">
                                <div className="col-md-6">
                                  <div className="comment-form-style">
                                    <input placeholder="Name" type="text" />
                                  </div>
                                </div>
                                <div className="col-md-6">
                                  <div className="comment-form-style">
                                    <input placeholder="Email" type="email" />
                                  </div>
                                </div>
                                <div className="col-md-12">
                                  <div className="comment-form-style form-submit">
                                    <textarea name="Your Comment" placeholder="Comment"></textarea>
                                    <button className="btn btn-primary btn-hover-color-primary " type="submit" value="Submit">Submit Comment</button>
                                  </div>
                                </div>
                              </div>
                            </form>
                          </div>
                        </div>
                      </div>
                      
                    </div>
                  </div>
                </div>
              </div>
              </div>
              {/* Related Products */}
      <div className="product-area related-product" style={{marginTop: '100px'}}>
        <div className="container">
          <div className="row">
            <div className="col-12">
              <div className="section-title text-center m-0">
                <h2 className="title">Related Products</h2>
                <p>There are many variations of passages of Lorem Ipsum available</p>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col">
              <div className="new-product-slider swiper-container slider-nav-style-1">
                <div className="swiper-wrapper">
                  {relatedProducts.map((relatedProduct) => (
                    <div key={relatedProduct.id} className="swiper-slide" style={{width: '275px'}}>
                      {/* Single Product */}
                      <div className="product" style={{width:'270px', height: '400px'}}>
                      <div class="thumb">
                                            <a href={`${relatedProduct.id}`} class="image">
                                                <img src={relatedProduct.mainImage} alt="Product" />
                                                <img class="hover-image" src={relatedProduct.mainImage} alt="Product" />
                                            </a>
                                        </div>
                                        <div class="content">
                                            <span class="category"><a href={`{relatedProduct.id}`}>{relatedProduct.category.name}</a></span>
                                            <h5 class="title"><a href={`${relatedProduct.id}`}>{relatedProduct.name}
                                                </a>
                                            </h5>
                                            <span class="price">
                                            <span class="new">{relatedProduct.cost}</span>
                                            </span>
                                        </div>
                                        <div class="actions">
                                            <button title="Add To Cart" class="action add-to-cart" data-bs-toggle="modal" data-bs-target="#exampleModal-Cart"><i
                                                class="pe-7s-shopbag"></i></button>
                                            <button class="action wishlist" title="Wishlist" data-bs-toggle="modal" data-bs-target="#exampleModal-Wishlist"><i
                                                    class="pe-7s-like"></i></button>
                                        </div>
                        <Link to={`${relatedProduct.id}`} className="image">
                          {/* Product image goes here */}
                        </Link>
                        {/* Other product details go here */}
                      </div>
                    </div>
                  ))}
                </div>
                {/* Add Arrows */}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
            </div>
          </div>
  );
};

export default SingleProduct;
