// Добавляем импорт useState и useEffect
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [purchaseHistory, setPurchaseHistory] = useState([]);
  const [orderStatus, setOrderStatus] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchPurchaseHistory = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/purchase-history', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setPurchaseHistory(response.data);
      } catch (error) {
        console.error('Error fetching purchase history:', error);
      }
    };

    fetchPurchaseHistory();
  }, []);

  useEffect(() => {
    const fetchOrderStatus = async () => {
      try {
        const response = await axios.get('http://localhost:5000/orders/status', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setOrderStatus(response.data);
      } catch (error) {
        console.error('Error fetching order status:', error);
      }
    };

    fetchOrderStatus();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  const handlePurchaseStory = () => {
    // Добавим логику для отображения истории покупок
    console.log(purchaseHistory);
  };

  const handleTrackOrder = () => {
    // Добавим логику для отслеживания статуса заказа
    console.log(orderStatus);
  };

  return (
    <>
      <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h2 className="breadcrumb-title">Profile</h2>
              <ul className="breadcrumb-list">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">Profile</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
      <div className="container mt-5">
        {userData && (
          <div>
            <h2>{userData.username}'s Profile</h2>
            <p>Email: {userData.email}</p>
            <p>Registration Date: {new Date(userData.createdAt).toLocaleDateString()}</p>
          </div>
        )}
        <div>
          <button className="btn btn-primary mt-4 mr-3" onClick={handlePurchaseStory}>
            Purchase Story
          </button>
          <button className="btn btn-info mt-4" onClick={handleTrackOrder}>
            Track Order
          </button>
        </div>
        <button className="btn btn-danger mt-4" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </>
  );
};

export default Profile;
