import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const BrandList = () => {
  const [brands, setBrands] = useState([]);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    // Получите данные о пользователе, включая роль, с вашего сервера.
    // Замените 'http://localhost:5000/users/me' на ваш реальный эндпоинт.
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role); // Предполагается, что роль приходит в ответе с сервера.
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    // Здесь вы можете использовать ваш API для получения списка брендов
    fetch('http://localhost:5000/brand')
      .then((response) => response.json())
      .then((data) => setBrands(data))
      .catch((error) => console.error('Error fetching brands:', error));
  }, []);

  const handleDeleteBrand = (id) => {
    // Здесь вы можете использовать ваш API для удаления бренда
    fetch(`http://localhost:5000/brand/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        // После успешного удаления обновите список брендов
        const updatedBrands = brands.filter((brand) => brand.id !== id);
        setBrands(updatedBrands);
      })
      .catch((error) => console.error('Error deleting brand:', error));
  };

  return (
    <div className="container mt-5">
        {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
      <div className="mb-3">
        <Link to="/admin/brands/add" className="btn btn-primary">
          Добавить новый бренд
        </Link>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Название бренда</th>
            <th scope="col">Действия</th>
          </tr>
        </thead>
        <tbody>
          {brands.map((brand) => (
            <tr key={brand.id}>
              <th scope="row">{brand.id}</th>
              <td>{brand.name}</td>
              <td>
                <Link to={`/admin/brands/edit/${brand.id}`} className="btn btn-warning me-2">
                  Изменить
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default BrandList;
