// TrackOrder.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TrackOrder = () => {
  const [order, setOrder] = useState(null);
  const orderNum = '123'; // Здесь должен быть номер заказа, который пользователь хочет отследить

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/orders/track-order/${orderNum}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        setOrder(response.data);
      } catch (error) {
        console.error('Error fetching order:', error);
      }
    };

    fetchOrder();
  }, []);

  return (
    <div className="container">
      <h2>Track Order</h2>
      {/* Вывод информации о заказе и его статусе */}
    </div>
  );
};

export default TrackOrder;
