import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [purchaseHistory, setPurchaseHistory] = useState([]);
  const [orderStatus, setOrderStatus] = useState([]);
  const [showOrders, setShowOrders] = useState(false); // Состояние для отслеживания видимости информации о заказах

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchPurchaseHistory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/orders/${userData.email}/purchasehistory`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setPurchaseHistory(response.data);
      } catch (error) {
        console.error('Error fetching purchase history:', error);
      }
    };

    if (userData) {
      fetchPurchaseHistory();
    }
  }, [userData]);

  useEffect(() => {
    const fetchOrderStatus = async () => {
      try {
        const response = await axios.get('http://localhost:5000/orders', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        // Фильтрация статусов заказов по адресу электронной почты пользователя
        const userOrders = response.data.filter(order => order.customers.email === userData.email);

        setOrderStatus(userOrders);
      } catch (error) {
        console.error('Error fetching order status:', error);
      }
    };

    fetchOrderStatus();
  }, [userData]);

  const handleLogout = () => {
    // Удаляем токен из локального хранилища
    localStorage.removeItem('token');
    // Перенаправляем пользователя на страницу входа
    window.location.href = '/login';
  };

  return (
    <>
      {/* Breadcrumb Area */}
      <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h2 className="breadcrumb-title">Profile</h2>
              <ul className="breadcrumb-list">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">Profile</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-4">
            {/* Кнопки слева */}
            <div className="list-group">
              <button className="list-group-item list-group-item-action" onClick={() => console.log(purchaseHistory)}>Purchase Story</button>
              <button className="list-group-item list-group-item-action" onClick={() => setShowOrders(!showOrders)}>Track Order</button>
              <button className="list-group-item list-group-item-action" onClick={() => console.log('Profile clicked')}>Profile</button>
              <button className="list-group-item list-group-item-action" onClick={handleLogout}>Logout</button>
            </div>
          </div>
          <div className="col-md-8">
            {/* Информация о пользователе и заказах справа */}
            {userData && (
              <div>
                <h2>{userData.username}'s Profile</h2>
                <p>Email: {userData.email}</p>
                <p>Registration Date: {new Date(userData.createdAt).toLocaleDateString()}</p>
                {/* Дополнительные данные о пользователе, которые у вас есть в базе данных */}
              </div>
            )}

            {/* Отображение истории покупок пользователя */}
            {purchaseHistory.length > 0 && (
              <div>
                <h3 className="mt-4">Purchase History</h3>
                <ul className="list-group">
                  {purchaseHistory.map((order) => (
                    <li key={order.orderNum} className="list-group-item">
                      <strong>Order {order.orderNum}</strong> - Total: ${order.total}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* Отображение статуса заказов пользователя */}
            {showOrders && orderStatus.length > 0 && (
              <div>
                <h3 className="mt-4">Order Status</h3>
                <ul className="list-group">
                  {orderStatus.map((order) => (
                    <li key={order.id} className="list-group-item">
                      <strong>Order {order.id}</strong> - {order.status.name}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Profile;
