// WishlistContext.jsx
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const WishlistContext = createContext();

export const WishlistProvider = ({ children }) => {
  const getWishlistFromLocalStorage = () => {
    const storedWishlist = localStorage.getItem('wishlist');
    return storedWishlist ? JSON.parse(storedWishlist) : [];
  };

  const saveWishlistToLocalStorage = (wishlist) => {
    localStorage.setItem('wishlist', JSON.stringify(wishlist));
  };

  const [wishlistItems, setWishlistItems] = useState(() => getWishlistFromLocalStorage());

  const addToWishlist = async (itemId, quantity = 1) => {
    try {
      const existingItem = wishlistItems.find((item) => item.id === itemId);

      if (existingItem) {
        // Если продукт уже есть в списке желаемого, увеличиваем количество
        setWishlistItems((prevItems) =>
          prevItems.map((item) =>
            item.id === itemId ? { ...item, quantity: item.quantity + quantity } : item
          )
        );
      } else {
        // Иначе добавляем новый продукт в список желаемого
        const response = await axios.get(`http://localhost:5000/products/${itemId}`);
        const newItem = { ...response.data, quantity };
        setWishlistItems((prevItems) => [...prevItems, newItem]);
      }
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  const removeFromWishlist = (itemId) => {
    setWishlistItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
  };

  useEffect(() => {
    // Перемещаем внутрь WishlistProvider
    const prevItems = getWishlistFromLocalStorage();
    setWishlistItems(prevItems);
  }, []); // Добавляем зависимость, чтобы useEffect выполнялся только при монтировании компонента

  return (
    <WishlistContext.Provider value={{ wishlistItems, addToWishlist, removeFromWishlist }}>
      {children}
    </WishlistContext.Provider>
  );
};

export const useWishlist = () => {
  const context = useContext(WishlistContext);
  if (!context) {
    throw new Error('useWishlist must be used within a WishlistProvider');
  }
  return context;
};
