import React, { useState, useEffect } from 'react';
import { useCart } from '../components/CartContext';

const Checkout = () => {
    const { cartItems, cartQuantity } = useCart();
    const [cities, setCities] = useState([]);
    const [selectedCity, setSelectedCity] = useState('');
  
    useEffect(() => {
      // Функция для получения списка городов с сервера
      const fetchCities = async () => {
        try {
          const response = await fetch('http://localhost:5000/city');
          const data = await response.json();
          setCities(data);
        } catch (error) {
          console.error('Error fetching cities:', error);
        }
      };
  
      // Вызовем функцию получения городов
      fetchCities();
    }, []); // Пустой массив означает, что useEffect сработает только при монтировании компонента

  return (
    <div className="checkout-area pt-100px pb-100px">
      <div className="container">
        {/* Ваш остальной код */}
        <div className="row">
          <div className="col-lg-7">
            <div className="billing-info-wrap">
              <h3>Billing Details</h3>
              {/* ... (остальной код) */}
            </div>
          </div>
          <div className="col-lg-5 mt-md-30px mt-lm-30px">
            <div className="your-order-area">
              <h3>Your order</h3>
              <div className="your-order-wrap gray-bg-4">
                <div className="your-order-product-info">
                  <div className="your-order-top">
                    <ul>
                      <li>Product</li>
                      <li>Total</li>
                    </ul>
                  </div>
                  <div className="your-order-middle">
                    <ul>
                      {cartItems.map((item) => (
                        <li key={item.id}>
                          <span className="order-middle-left">{item.name} X {item.quantity}</span>{' '}
                          <span className="order-price">${item.price * item.quantity} </span>
                        </li>
                      ))}
                    </ul>
                  </div>
                  <div className="your-order-bottom">
                    <ul>
                      <li className="your-order-shipping">Shipping</li>
                      <li>Free shipping</li>
                    </ul>
                  </div>
                  <div className="your-order-total">
                    <ul>
                      <li className="order-total">Total</li>
                      <li>${cartItems.reduce((total, item) => total + item.price * item.quantity, 0)}</li>
                    </ul>
                  </div>
                </div>
                <div className="payment-method">
                  {/* ... (остальной код) */}
                </div>
              </div>
              <div className="Place-order mt-25">
                <a className="btn-hover" href="#">
                  Place Order
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
