// login.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const history = useHistory();
  const [loginDetails, setLoginDetails] = useState({
    email: '',
    password: '',
  });

  const handleLoginChange = (e) => {
    setLoginDetails({ ...loginDetails, [e.target.name]: e.target.value });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/users/login', loginDetails);
      console.log(response.data);
      // Дополнительные действия после успешного входа
      history.push('/dashboard'); // Перенаправление на другую страницу после входа
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const [registerDetails, setRegisterDetails] = useState({
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    confPassword: '',
  });

  const handleRegisterChange = (e) => {
    setRegisterDetails({ ...registerDetails, [e.target.name]: e.target.value });
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/users/register', registerDetails);
      console.log(response.data);
      // Дополнительные действия после успешной регистрации
    } catch (error) {
      console.error('Error registering:', error);
    }
  };

  return (
    <div className="login-register-area pt-100px pb-100px">
      <div className="container">
        <div className="row">
          <div className="col-lg-7 col-md-12 ml-auto mr-auto">
            <div className="login-register-wrapper">
              <div className="login-register-tab-list nav">
                <a className="active" data-bs-toggle="tab" href="#lg1">
                  <h4>Login</h4>
                </a>
                <a data-bs-toggle="tab" href="#lg2">
                  <h4>Register</h4>
                </a>
              </div>
              <div className="tab-content">
                <div id="lg1" className="tab-pane active">
                  <div className="login-form-container">
                    <div className="login-register-form">
                      <form onSubmit={handleLoginSubmit}>
                        {/* Ваш код для формы входа */}
                      </form>
                    </div>
                  </div>
                </div>
                <div id="lg2" className="tab-pane">
                  <div className="login-form-container">
                    <div className="login-register-form">
                      <form onSubmit={handleRegisterSubmit}>
                        {/* Измененные поля для формы регистрации */}
                        <input type="text" name="firstname" placeholder="First Name" onChange={handleRegisterChange} />
                        <input type="text" name="lastname" placeholder="Last Name" onChange={handleRegisterChange} />
                        {/* Остальные поля для формы регистрации */}
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
