import React, { useState, useEffect } from 'react';
import { useCart } from '../components/CartContext';

const Checkout = () => {
  const { cartItems, cartQuantity } = useCart();
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [billingDetails, setBillingDetails] = useState({
    firstName: '',
    lastName: '',
    streetAddress: '',
    phone: '',
    email: '',
  });

  const handleBillingDetailsChange = (e) => {
    setBillingDetails({ ...billingDetails, [e.target.name]: e.target.value });
  };

  const handlePlaceOrder = async () => {
    // Генерация случайных чисел для checknum и ordersnum
    const checkNum = Math.floor(Math.random() * 1000000).toString();
    const orderNum = Math.floor(Math.random() * 1000000).toString();

    // Создание кастомера
    const customerResponse = await fetch('http://localhost:5000/customers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: `${billingDetails.firstName} ${billingDetails.lastName}`,
        phone: billingDetails.phone,
        email: billingDetails.email,
        address: billingDetails.streetAddress,
        city_id: selectedCity,
      }),
    });

    console.log('Данные клиента:', JSON.stringify({
      name: `${billingDetails.firstName} ${billingDetails.lastName}`,
      phone: billingDetails.phone,
      email: billingDetails.email,
      address: billingDetails.streetAddress,
      city_id: selectedCity,
    }));
    const customerData = await customerResponse.json();
    const customerId = customerData.id;

    // Создание заказа
    const orderResponse = await fetch('http://localhost:5000/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        customer_id: customerId,
        date: new Date(),
        status_id: 1, // Установите соответствующий статус
      }),
    });
    const orderData = await orderResponse.json();
    const orderId = orderData.id;

    // Создание ордердетаилс
    for (const item of cartItems) {
      await fetch('http://localhost:5000/orderdetails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderNum: orderNum,
          prod_code: item.id, // Используйте соответствующий атрибут товара
          productQuantity: item.quantity,
        }),
      });
    }

    // Создание пейментс
    await fetch('http://localhost:5000/payments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        customer_id: customerId,
        checkNum: checkNum,
        payment: new Date(),
        amount: cartItems.reduce((total, item) => total + item.cost * item.quantity, 0),
      }),
    });

    // Очистка корзины
    // (Добавьте соответствующий код для очистки корзины)

    // Дополнительные действия, если необходимо
    // (Добавьте свой код в зависимости от потребностей)

    // Перенаправление или отображение подтверждения
  };

  useEffect(() => {
    // Функция для получения списка городов с сервера
    const fetchCities = async () => {
      try {
        const response = await fetch('http://localhost:5000/city');
        const data = await response.json();
        setCities(data);
      } catch (error) {
        console.error('Error fetching cities:', error);
      }
    };

    // Вызовем функцию получения городов
    fetchCities();
  }, []); // Пустой массив означает, что useEffect сработает только при монтировании компонента


  return (
    <>
          {/* Breadcrumb Area */}
          <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h2 className="breadcrumb-title">Checkout</h2>
              <ul className="breadcrumb-list">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">Checkout</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    <div className="checkout-area pt-100px pb-100px">
      <div className="container">
        {/* Ваш остальной код */}
        <div className="row">
        <div class="col-lg-7">
                        <div class="billing-info-wrap">
                            <h3>Billing Details</h3>
                            <div class="row">
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>First Name</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Last Name</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div className="col-lg-12">
                  <div className="billing-select mb-4">
                    <label>City</label>
                    <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
                      <option>Select a city</option>
                      {cities.map((city) => (
                        <option key={city.id} value={city.name}>
                          {city.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                                <div class="col-lg-12">
                                    <div class="billing-info mb-4">
                                        <label>Street Address</label>
                                        <input class="billing-address" placeholder="House number and street name" type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Phone</label>
                                        <input type="text" />
                                    </div>
                                </div>
                                <div class="col-lg-6 col-md-6">
                                    <div class="billing-info mb-4">
                                        <label>Email Address</label>
                                        <input type="text" />
                                    </div>
                                </div>
                            </div>
                        </div>
          </div>
          <div className="col-lg-5 mt-md-30px mt-lm-30px">
            <div className="your-order-area">
              <h3>Your order</h3>
              <div className="your-order-wrap gray-bg-4">
                <div className="your-order-product-info">
                  <div className="your-order-top">
                    <ul>
                      <li>Product</li>
                      <li>Total</li>
                    </ul>
                  </div>
                  <div className="your-order-middle">
                    <ul>
                      {cartItems.map((item) => (
                        <li key={item.id}>
                          <span className="order-middle-left">{item.name} X {item.quantity}</span>{' '}
                          <span className="order-price">${item.cost * item.quantity} </span>
                        </li>
                      ))}
                    </ul>
                  </div>
                  <div className="your-order-bottom">
                    <ul>
                      <li className="your-order-shipping">Shipping</li>
                      <li>Free shipping</li>
                    </ul>
                  </div>
                  <div className="your-order-total">
                    <ul>
                      <li className="order-total">Total</li>
                      <li>${cartItems.reduce((total, item) => total + item.cost * item.quantity, 0)}</li>
                    </ul>
                  </div>
                </div>
                <div className="payment-method">
                  {/* ... (остальной код) */}
                </div>
              </div>
              <div className="Place-order mt-25">
              <a className="btn-hover" href="#" onClick={handlePlaceOrder}>
                    Place Order
                  </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};

export default Checkout;
