import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    // Удаляем токен из локального хранилища
    localStorage.removeItem('token');
    // Перенаправляем пользователя на страницу входа
    window.location.href = '/login';
  };

  const handlePurchaseHistory = () => {
    history.push('/purchase-history');
  };

  const handleTrackOrder = () => {
    // Здесь предполагается, что у вас есть доступ к номеру заказа
    // Допустим, у вас есть список заказов, и пользователь может выбрать заказ для отслеживания
    // Предположим, что orderNum хранится в состоянии компонента или передается в функцию
    const orderNum = '123456'; // Вместо '123456' подставьте актуальный номер заказа
    history.push(`/track-order/${orderNum}`);
  };
  return (
    <>
      {/* Breadcrumb Area */}
      <div className="breadcrumb-area" style={{ backgroundImage: `url(https://template.hasthemes.com/hmart/hmart/assets/images/about/breadcrunb-bg.webp)` }}>
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h2 className="breadcrumb-title">Profile</h2>
              <ul className="breadcrumb-list">
                <li className="breadcrumb-item"><a href="/">Home</a></li>
                <li className="breadcrumb-item active">Profile</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
      <div className="container mt-5">
        {/* Информация о пользователе */}
        {userData && (
          <div>
            <h2>{userData.username}'s Profile</h2>
            <p>Email: {userData.email}</p>
            <p>Registration Date: {new Date(userData.createdAt).toLocaleDateString()}</p>
            {/* Дополнительные данные о пользователе, которые у вас есть в базе данных */}
          </div>
        )}

        {/* Кнопки "Purchase Story" и "Track Order" */}
        <div className="mt-4">
          <button className="btn btn-primary mr-3" onClick={handlePurchaseHistory}>
            Purchase Story
          </button>
          <button className="btn btn-info" onClick={handleTrackOrder}>
            Track Order
          </button>
        </div>

        {/* Кнопка для выхода из аккаунта */}
        <button className="btn btn-danger mt-4" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </>
  );
};

export default Profile;
