import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EditProduct = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({
    name: '',
    category_id: '',
    brand_id: '',
    description: '',
    cost: '',
    mainImage: '',
    additionalImages: [],
  });
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/products/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product data:', error);
      }
    };

    fetchProductData();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleAdditionalImagesChange = (e) => {
    const files = Array.from(e.target.files);
    const images = files.map((file) => URL.createObjectURL(file));
    setProduct({ ...product, additionalImages: images });
  };

  const handleAddImageInput = () => {
    setProduct({
      ...product,
      additionalImages: [...product.additionalImages, ''],
    });
  };

  const handleImageInputChange = (index, value) => {
    const updatedImages = [...product.additionalImages];
    updatedImages[index] = value;
    setProduct({ ...product, additionalImages: updatedImages });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('category_id', product.category_id);
    formData.append('brand_id', product.brand_id);
    formData.append('description', product.description);
    formData.append('cost', product.cost);
    formData.append('mainImage', product.mainImage);
    product.additionalImages.forEach((image) => formData.append('additionalImages', image));

    try {
      const response = await fetch(`http://localhost:5000/products/${id}`, {
        method: 'PUT',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.ok) {
        window.location.href = '/admin/products';
      } else {
        console.error('Failed to update product');
      }
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  return (
    <div className="container mt-5">
      {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
          <h2>Редактировать товар</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Название</label>
              <input type="text" className="form-control" id="name" name="name" value={product.name} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="category" className="form-label">Категория</label>
              <input type="text" className="form-control" id="category" name="category_id" value={product.category_id} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="brand" className="form-label">Бренд</label>
              <input type="text" className="form-control" id="brand" name="brand_id" value={product.brand_id} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Описание</label>
              <textarea className="form-control" id="description" name="description" value={product.description} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="cost" className="form-label">Стоимость</label>
              <input type="number" className="form-control" id="cost" name="cost" value={product.cost} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="mainImage" className="form-label">Основное изображение</label>
              <input type="text" className="form-control" id="mainImage" name="mainImage" value={product.mainImage} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="additionalImages" className="form-label">Дополнительные изображения</label>
              {product.additionalImages.map((image, index) => (
                <div key={index} className="mb-2">
                  <input
                    type="text"
                    className="form-control"
                    value={image}
                    onChange={(e) => handleImageInputChange(index, e.target.value)}
                    required
                  />
                </div>
              ))}
              <button type="button" className="btn btn-secondary" onClick={handleAddImageInput}>Добавить изображение</button>
            </div>
            <button type="submit" className="btn btn-primary">Сохранить изменения</button>
          </form>
        </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default EditProduct;
