// Код компонента EditProduct.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EditProduct = () => {
  const { id } = useParams();
  const [name, setName] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [brandId, setBrandId] = useState('');
  const [description, setDescription] = useState('');
  const [cost, setCost] = useState('');
  const [mainImage, setMainImage] = useState('');
  const [additionalImages, setAdditionalImages] = useState([]);
  const [additionalImageInputs, setAdditionalImageInputs] = useState([]);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setUserRole(data.role);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchAdditionalImages = async () => {
      try {
        const response = await fetch(`http://localhost:5000/products/${id}/images`);
        const data = await response.json();
        setAdditionalImages(data); // Устанавливаем дополнительные изображения в состояние
        setAdditionalImageInputs(data.map(image => image.link)); // Устанавливаем доп. изображения в инпуты
      } catch (error) {
        console.error('Error fetching additional images:', error);
      }
    };

    fetchAdditionalImages();
  }, [id]); // При изменении id запрашиваем новые дополнительные изображения

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/products/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setName(data.name);
        setCategoryId(data.category_id);
        setBrandId(data.brand_id);
        setDescription(data.description);
        setCost(data.cost);
        setMainImage(data.mainImage);
      } catch (error) {
        console.error('Error fetching product data:', error);
      }
    };

    fetchProductData();
  }, [id]);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setCategoryId(e.target.value);
  };

  const handleBrandChange = (e) => {
    setBrandId(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleCostChange = (e) => {
    setCost(e.target.value);
  };

  const handleMainImageChange = (e) => {
    setMainImage(e.target.value);
  };

  const handleAdditionalImageInputChange = (index, e) => {
    const newInputs = [...additionalImageInputs];
    newInputs[index] = e.target.value;
    setAdditionalImageInputs(newInputs);
  };

  const handleAddImageInput = () => {
    setAdditionalImageInputs([...additionalImageInputs, '']);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('category_id', categoryId);
    formData.append('brand_id', brandId);
    formData.append('description', description);
    formData.append('cost', cost);
    formData.append('mainImage', mainImage);
    additionalImageInputs.forEach(link => formData.append('additionalImages', link));

    try {
      const response = await fetch(`http://localhost:5000/products/${id}`, {
        method: 'PUT',
        body: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.ok) {
        window.location.href = '/admin/products';
      } else {
        console.error('Failed to update product');
      }
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  return (
    <div className="container mt-5">
      {userRole === 'admin' || userRole === 'client_manager' ? (
        <>
          <h2>Редактировать товар</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Название</label>
              <input type="text" className="form-control" id="name" value={name} onChange={handleNameChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="categoryId" className="form-label">Категория</label>
              <input type="text" className="form-control" id="categoryId" value={categoryId} onChange={handleCategoryChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="brandId" className="form-label">Бренд</label>
              <input type="text" className="form-control" id="brandId" value={brandId} onChange={handleBrandChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Описание</label>
              <textarea className="form-control" id="description" value={description} onChange={handleDescriptionChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="cost" className="form-label">Стоимость</label>
              <input type="number" className="form-control" id="cost" value={cost} onChange={handleCostChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="mainImage" className="form-label">Главное изображение</label>
              <input type="text" className="form-control" id="mainImage" value={mainImage} onChange={handleMainImageChange} required />
            </div>
            <div className="mb-3">
              <label htmlFor="additionalImages" className="form-label">Дополнительные изображения</label>
              {additionalImageInputs.map((link, index) => (
                <input key={index} type="text" className="form-control mb-2" value={link} onChange={(e) => handleAdditionalImageInputChange(index, e)} />
              ))}
              <button type="button" className="btn btn-secondary" onClick={handleAddImageInput}>Добавить изображение</button>
            </div>
            <button type="submit" className="btn btn-primary">Сохранить изменения</button>
          </form>
        </>
      ) : (
        <p>У вас нет прав для просмотра этой страницы.</p>
      )}
    </div>
  );
};

export default EditProduct;
